// src/components/Local Manager Dashboard/LocManagerDashboard.js
import React, { useEffect, useState } from 'react';
import LocManagerSidebar from './LocManagerSidebar';
import './LocManagerDashboard.css';
import { FiSearch, FiHelpCircle, FiBell } from "react-icons/fi";
import Chart from 'chart.js/auto';

let utciChartInstance = null;
let tempChartInstance = null;

const LocManagerDashboard = () => {
  const [data, setData] = useState({
    temperature: '',
    humidity: '',
    utci: '',
    heatStress: '',
    mitigation: '',
    utciGroup: ''
  });

  const [showUTCIChart, setShowUTCIChart] = useState(true);

  const toggleChart = () => {
    setShowUTCIChart(prev => !prev);
  };

  useEffect(() => {
    fetch('/data.csv')
      .then(res => res.text())
      .then(text => {
        const rows = text.trim().split('\n').slice(1);
        const randomRow = rows[Math.floor(Math.random() * rows.length)];
        const [temp, humidity, , utci, heatStress, , mitigation] = randomRow.split(',');

        const utciValue = parseFloat(utci);
        let utciGroup = '';
        if (utciValue <= 20) utciGroup = "1";
        else if (utciValue <= 30) utciGroup = "2";
        else if (utciValue <= 40) utciGroup = "3";
        else utciGroup = "4";

        setData({
          temperature: temp,
          humidity: humidity,
          utci,
          heatStress,
          mitigation,
          utciGroup
        });

        const daysOfWeek = ["Mon", "Tue", "Wed", "Thu", "Fri", "Sat", "Sun"];
        let trendRows = [];
        let usedIndices = new Set();

        while (trendRows.length < 7 && usedIndices.size < rows.length) {
          const i = Math.floor(Math.random() * rows.length);
          if (!usedIndices.has(i)) {
            usedIndices.add(i);
            trendRows.push(rows[i]);
          }
        }

        // Destroy existing chart instances
        if (utciChartInstance) utciChartInstance.destroy();
        if (tempChartInstance) tempChartInstance.destroy();

        const utciGroups = trendRows.map(row => {
          const val = parseFloat(row.split(',')[3]);
          if (val <= 20) return 1;
          if (val <= 30) return 2;
          if (val <= 40) return 3;
          return 4;
        });

        // UTCI Chart
        const ctx1 = document.getElementById('utciChart').getContext('2d');
        utciChartInstance = new Chart(ctx1, {
          type: 'line',
          data: {
            labels: daysOfWeek,
            datasets: [{
              label: 'Heat Risk (Line)',
              data: utciGroups,
              fill: false,
              borderColor: 'rgba(255, 99, 132, 1)',
              tension: 0.3,
              borderWidth: 2,
              pointRadius: 4
            }]
          },
          options: {
            responsive: true,
            scales: {
              y: {
                min: 1,
                max: 4,
                ticks: {
                  stepSize: 1,
                  callback: value => `${value}`
                },
                title: { display: true, text: 'Heat Risk Value' }
              },
              x: {
                title: { display: true, text: 'Day of the Week' }
              }
            }
          }
        });

        // Temperature & Humidity Chart
        const ctx2 = document.getElementById('tempHumidityChart').getContext('2d');
        tempChartInstance = new Chart(ctx2, {
          type: 'line',
          data: {
            labels: daysOfWeek,
            datasets: [
              {
                label: 'Temperature (°C)',
                data: trendRows.map(row => parseFloat(row.split(",")[0])),
                borderColor: 'rgba(255, 99, 132, 1)',
                backgroundColor: 'rgba(255, 99, 132, 0.2)',
                tension: 0.4
              },
              {
                label: 'Humidity (%)',
                data: trendRows.map(row => parseFloat(row.split(",")[1])),
                borderColor: 'rgba(54, 162, 235, 1)',
                backgroundColor: 'rgba(54, 162, 235, 0.2)',
                tension: 0.4
              }
            ]
          },
          options: {
            responsive: true,
            scales: {
              y: {
                beginAtZero: false,
                title: { display: true, text: 'Values' }
              },
              x: {
                title: { display: true, text: 'Day of the Week' }
              }
            }
          }
        });
      });
  }, []);

  const getRiskColorClass = (group) => {
    switch (group) {
      case "1": return "bg-success";
      case "2": return "bg-info";
      case "3": return "bg-warning";
      case "4": return "bg-danger";
      default: return "";
    }
  };

  return (
    <div className="locmanager-dashboard">
      <LocManagerSidebar />
      <div className="locmanager-main-content">
        <div className="locmanager-topbar">
          <div className="locmanager-search-bar">
            <FiSearch className="search-icon" />
            <input type="text" placeholder="Search" />
          </div>
          <div className="topbar-right">
            <span className="location-text">Current Location: Amsterdam Brewery</span>
            <FiHelpCircle className="topbar-icon" />
            <FiBell className="topbar-icon" />
            <button className="report-button">View Daily Report</button>
          </div>
        </div>

        {/* Dashboard Cards */}
        <div className="dashboard-cards">
          <div className={`card text-white heat-risk-card ${getRiskColorClass(data.utciGroup)}`}>
            <h4>Heat Stress Risk Level</h4>
            <p>{data.utciGroup}</p>
          </div>
          <div className="card bg-warning text-white">
            <h4>Temperature</h4>
            <p>{data.temperature} °C</p>
          </div>
          <div className="card bg-primary text-white">
            <h4>Humidity</h4>
            <p>{data.humidity} %</p>
          </div>
        </div>

        {/* Chart Toggle */}
        <div className="chart-toggle-bar" style={{ marginBottom: '12px' }}>
          <button className="report-button" onClick={toggleChart}>
            Switch Graph
          </button>
        </div>

        {/* Charts */}
        <div className="chart-container">
          {showUTCIChart ? (
            <canvas id="utciChart"></canvas>
          ) : (
            <canvas id="tempHumidityChart"></canvas>
          )}
        </div>

        {/* Mitigation Measures */}
        <div className="mitigation-panel">
          <h3 className="mitigation-title">Mitigation Measures</h3>
          <div className="mitigation-card">
            <table className="mitigation-table">
              <thead>
                <tr>
                  <th>Condition</th>
                  <th>Recommended Action</th>
                </tr>
              </thead>
              <tbody>
                <tr>
                  <td>{data.heatStress}</td>
                  <td>{data.mitigation}</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default LocManagerDashboard;
