import React, { useEffect, useState } from 'react';
import LocManagerSidebar from './LocManagerSidebar';
import './LocManagerDashboard.css';
import { FiSearch, FiHelpCircle, FiBell } from "react-icons/fi";
import Papa from 'papaparse';

const NotificationsPage = () => {
  const [mitigation, setMitigation] = useState('');
  const [notifications, setNotifications] = useState([]);
  const [confirmation, setConfirmation] = useState({});
  const [reportedSymptoms, setReportedSymptoms] = useState([]);

  useEffect(() => {
    // Load mitigation suggestion
    Papa.parse('/data.csv', {
      download: true,
      header: true,
      complete: (results) => {
        const rows = results.data;
        const randomRow = rows[Math.floor(Math.random() * rows.length)];
        const message = randomRow["Mitigation"] || "No mitigation found.";
        setMitigation(message);
      },
      error: (err) => {
        console.error("Error loading mitigation data:", err);
        setMitigation("Failed to load mitigation data.");
      }
    });

    // Load previous notifications
    const saved = JSON.parse(localStorage.getItem('notifications')) || [];
    setNotifications(saved);

    // Load confirmation checkboxes
    const confirmMap = JSON.parse(localStorage.getItem('confirmations')) || {};
    setConfirmation(confirmMap);

    // Load symptoms reported by workers
    const symptoms = JSON.parse(localStorage.getItem('reportedSymptoms')) || [];
    setReportedSymptoms(symptoms);
  }, []);

  const handleSendNotification = () => {
    if (!mitigation || mitigation.includes("No mitigation") || mitigation.includes("Failed")) return;

    const updated = [mitigation, ...notifications];
    const newConfirmation = { ...confirmation, [mitigation]: false };

    setNotifications(updated);
    setConfirmation(newConfirmation);

    localStorage.setItem('notifications', JSON.stringify(updated));
    localStorage.setItem('confirmations', JSON.stringify(newConfirmation));
    localStorage.setItem('notifications_sent', (parseInt(localStorage.getItem("notifications_sent") || "0") + 1).toString());

    setMitigation('');
  };

  const handleClearNotifications = () => {
    localStorage.removeItem('notifications');
    localStorage.removeItem('confirmations');
    setNotifications([]);
    setConfirmation({});
  };

  const toggleConfirmation = (note) => {
    const updated = { ...confirmation, [note]: !confirmation[note] };
    setConfirmation(updated);
    localStorage.setItem('confirmations', JSON.stringify(updated));
  };

  return (
    <div className="locmanager-dashboard">
      <LocManagerSidebar />
      <div className="locmanager-main-content">
        {/* Top Bar */}
        <div className="locmanager-topbar">
          <div className="locmanager-search-bar">
            <FiSearch className="search-icon" />
            <input type="text" placeholder="Search" />
          </div>
          <div className="topbar-right">
            <span className="location-text">Current Location: Amsterdam Brewery</span>
            <FiHelpCircle className="topbar-icon" />
            <FiBell className="topbar-icon" />
            <button className="report-button">View Daily Report</button>
          </div>
        </div>

        <h2>Notifications</h2>
        <h4>Suggested Mitigation Based on Todayâ€™s Heat Risk</h4>
        <div style={{ background: '#e0f7ff', padding: '12px', borderRadius: '8px', marginBottom: '16px' }}>
          {mitigation}
        </div>

        <button className="report-button" onClick={handleSendNotification}>Send Notification</button>

        {/* Sent Notifications */}
        <h4 style={{ marginTop: '30px' }}>Sent Notifications</h4>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          {notifications.length === 0 ? (
            <p>No notifications sent yet.</p>
          ) : (
            notifications.map((note, index) => (
              <div
                key={index}
                style={{
                  background: '#fff3cd',
                  padding: '10px',
                  borderRadius: '6px',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}
              >
                <span>{note}</span>
                <label style={{ fontSize: '0.9rem' }}>
                  <input
                    type="checkbox"
                    checked={confirmation[note] || false}
                    onChange={() => toggleConfirmation(note)}
                    style={{ marginRight: '8px' }}
                  />
                  Action Taken
                </label>
              </div>
            ))
          )}
        </div>

        {/* Clear Button */}
        <div style={{ display: 'flex', justifyContent: 'flex-end', marginTop: '16px' }}>
          <button
            className="report-button"
            style={{ backgroundColor: '#ff4d4f' }}
            onClick={handleClearNotifications}
          >
            Clear Notifications
          </button>
        </div>

        {/* Reported Symptoms Section */}
        <h4 style={{ marginTop: '40px' }}>Reported Symptoms by Workers</h4>
        <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
          {reportedSymptoms.length === 0 ? (
            <p>No symptoms reported yet.</p>
          ) : (
            reportedSymptoms.map((symptom, idx) => (
              <div
                key={idx}
                style={{
                  background: '#ffe6e6',
                  padding: '10px',
                  borderRadius: '6px'
                }}
              >
                {symptom}
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default NotificationsPage;
